import { Component, OnInit } from '@angular/core';
import { Task } from '../../models/task.model';
import { Router } from '@angular/router';

import { TaskCommunicationService } from '../../task-communication.service'; // Import the TaskCommunicationService

@Component({
  selector: 'app-todo',
  templateUrl: './todo.component.html',
  styleUrls: ['./todo.component.css']
})
export class TodoComponent {
  tasks: Task[]=[

  ];

  newTask: string = ''; // Initialize newTask property

  constructor(private router: Router, private taskCommunicationService: TaskCommunicationService) {
    // Subscribe to the taskAdded$ observable
    this.taskCommunicationService.taskAdded$.subscribe(task => {
      this.addTask(task); // Call the addTask function with the received task
    });
  }

  clickButton(path: string) {
    console.log("move")
    this.router.navigate([path]);
} 
  moveToNextPage() {
    this.router.navigate(['/form']); // Replace 'next-page' with your route path
  }
  //tasks: string[] = [];
  addTask(task: Task): void {
    this.tasks.push(task);
  }
  // addTask(): void {
  //   if (this.newTask.trim() !== '') {
  //     this.tasks.push({ id: this.tasks.length + 1, title: this.newTask, description: '', dueDate: new Date(), completed: false });
  //     this.newTask = '';
  //   }
 // }

  removeTask(index: number): void {
    this.tasks.splice(index, 1);
  }
}

<div class="todo-container">
  <h1>Todo List</h1>
  <button (click)="clickButton('/task-form')">Add </button>
  <!-- <input [(ngModel)]="newTask" placeholder="Add a new task" (keyup.enter)="addTask()" > -->
  <span><a  routerLink="/form"><i class="fas fa-plus custom-plusicon"></i></a></span>

  <ul>
    <li *ngFor="let task of tasks; let i = index">
      <app-todoitem [task]="task" (remove)="removeTask(i)"></app-todoitem>
    </li>
  </ul>
</div>
