import { Component } from '@angular/core';
import { NgForm } from '@angular/forms';
import { Task } from '../../models/task.model'; // Import the Task model

@Component({
  selector: 'app-task-form',
  templateUrl: './task-form.component.html',
  styleUrls: ['./task-form.component.css']
})
export class TaskFormComponent {

  newTask: Task = { id: 0, title: '', description: '', dueDate: new Date(), completed: false };// Initialize a new Task instance

  onSubmit(form: NgForm) {
    if (form.valid) {
      // Implement the logic to add the task
      // You can emit an event or call a service to add the task

    }
  }
}
<!-- In add-task.component.html -->
<div class="form-container">
  <h1>Add Task</h1>
  <form>
    <label for="title">Title</label>
    <input type="text" id="title" [(ngModel)]="newTask.title" name="title">

    <label for="description">Description</label>
    <textarea id="description" [(ngModel)]="newTask.description" name="description"></textarea>

    <!-- Add more form controls as needed -->

    <button type="submit">Add Task</button>
  </form>
</div>
