import { Component, OnInit } from '@angular/core';
import { Task } from '../../models/task.model';
@Component({
  selector: 'app-todo',
  templateUrl: './todo.component.html',
  styleUrls: ['./todo.component.css']
})
export class TodoComponent {
  tasks: Task[]=[

  ];

  newTask: string = ''; // Initialize newTask property

  //tasks: string[] = [];
  addTask(): void {
    if (this.newTask.trim() !== '') {
      this.tasks.push({ id: this.tasks.length + 1, title: this.newTask, description: '', dueDate: new Date(), completed: false });
      this.newTask = '';
    }
  }

  removeTask(index: number): void {
    this.tasks.splice(index, 1);
  }
}

<div class="todo-container">
  <h1>Todo List</h1>

  <input [(ngModel)]="newTask" placeholder="Add a new task" (keyup.enter)="addTask()">
  <ul>
    <li *ngFor="let task of tasks; let i = index">
      <app-todoitem [task]="task" (remove)="removeTask(i)"></app-todoitem>
    </li>
  </ul>
</div>
