import { Component } from '@angular/core';
import { NgForm } from '@angular/forms';
import { Task } from '../../models/task.model'; // Import the Task model
import { TaskCommunicationService } from '../../task-communication.service'; // Import the TaskCommunicationService

@Component({
  selector: 'app-task-form',
  templateUrl: './task-form.component.html',
  styleUrls: ['./task-form.component.css']
})
export class TaskFormComponent {

  newTask: Task = { id: 0, title: '', description: '', dueDate: new Date(), completed: false };// Initialize a new Task instance

  constructor(private taskCommunicationService: TaskCommunicationService, private router: Router) {} // Inject the service

  onSubmit(form: NgForm) {
    if (form.valid) {
      // Call the addTask method of the service
      this.taskCommunicationService.addTask(this.newTask);

      // Clear the form after submission
      this.newTask ={ id: 0, title: '', description: '', dueDate: new Date(), completed: false };// Initialize a new Task instance
      this.router.navigate(['/main-page']);
      form.resetForm();
    }
  }
}
