import { Component, OnInit } from '@angular/core';
import { Task } from '../../models/task.model';
@Component({
  selector: 'app-todo',
  templateUrl: './todo.component.html',
  styleUrls: ['./todo.component.css']
})
export class TodoComponent {
  tasks: Task[]=[]
  newTask: string = ''; // Initialize newTask property

  //tasks: string[] = [];
  addTask(newTaskTitle: string): void {
    if (newTaskTitle.trim() !== '') {
      const newTask = new Task(1, newTaskTitle, "33", new Date('2023-08-15'), false);
      this.tasks.push(newTask); // Add the new task to the tasks array
      this.newTask = ''; // Clear the input field after adding task
    }
  }

  removeTask(index: number): void {
    if (index >= 0 && index < this.tasks.length) {
      this.tasks.splice(index, 1);
    }
  }
}
<input type="text" [(ngModel)]="newTask">
<button (click)="addTask(newTask)">Add Task</button>

<ul>
  <li *ngFor="let task of tasks; let i = index">
    <app-todoitem [task]="task"></app-todoitem>
      <button (click)="removeTask(i)">Remove</button>
  </li>
</ul>
