import { Component, OnInit } from '@angular/core';
import { Task } from '../../models/task.model';
import { Router } from '@angular/router';
import { TaskCommunicationService } from '../../task-communication.service'; // Import the TaskCommunicationService

@Component({
  selector: 'app-todo',
  templateUrl: './todo.component.html',
  styleUrls: ['./todo.component.css']
})
export class TodoComponent {
  tasks: Task[] = [];

  constructor(private taskService: TaskCommunicationService,private router: Router) {}

  ngOnInit() {
    this.taskService.tasks$.subscribe(tasks => {
      this.tasks = tasks;
    });
  }

  addTask(newTask: Task) {
    this.taskService.addTask(newTask);
  }

  removeTask(index: number) {
    this.taskService.removeTask(index);
  }
  updateTask(updatedTask: Task, index: number) {
    this.taskService.updateTask(updatedTask, index);
  }

  clickButton(path: string) {
    console.log("move")
    this.router.navigate([path]);
} 
}

<div class="todo-container">
  <h1>Todo List</h1>
  <button (click)="clickButton('/task-form')">Add </button>
  <!-- <input [(ngModel)]="newTask" placeholder="Add a new task" (keyup.enter)="addTask()" > -->
  <!-- <span><a  routerLink="/form"><i class="fas fa-plus custom-plusicon"></i></a></span> -->

  <ul>
    <li *ngFor="let task of tasks; let i = index">
      <app-todoitem [task]="task" (remove)="removeTask(i)" (update)="updateTask(task,i)"> </app-todoitem>
    </li>
  </ul>
</div>
