import { Injectable } from '@angular/core';
import { BehaviorSubject } from 'rxjs';
import { Observable } from 'rxjs';
import { HttpClient } from '@angular/common/http';
import { Task } from './models/task.model'; // Import the Task model

@Injectable({
  providedIn: 'root'
})
export class TaskCommunicationService {
  private tasks: Task[] = []; // Array to hold tasks
  private tasksSubject = new BehaviorSubject<Task[]>(this.tasks);
  tasks$ = this.tasksSubject.asObservable();
  private apiUrl = 'your_api_url_here'; 
  constructor(private http: HttpClient) {}
  getTasks(): Task[] {
    return this.tasks;
  }

  addTask(task: Task) {
    this.tasks.push(task);
    this.tasksSubject.next(this.tasks);
  }

  removeTask(index: number) {
    this.tasks.splice(index, 1);
    this.tasksSubject.next(this.tasks);
  }
  updateTask(updatedTask: Task) {
    const index = this.tasks.findIndex(task => task.id === updatedTask.id);
    if (index !== -1) {
      this.tasks[index] = updatedTask;
      this.tasksSubject.next(this.tasks);
    }
  }
  getTask(taskId: number): Observable<Task> {
    const url = `${this.apiUrl}/tasks/${taskId}`; // Assuming you have an 'tasks' endpoint
    return this.http.get<Task>(url);
  }

}
