import { Component, Input, Output, EventEmitter } from '@angular/core';
import { Task } from '../../models/task.model';
import { Router } from '@angular/router';

@Component({
  selector: 'app-todoitem',
  templateUrl: './todoitem.component.html',
  styleUrls: ['./todoitem.component.css']
})
export class TodoitemComponent {

  constructor(private router: Router){

  }
  @Input() task: Task = { id: 0, title: '', description: '', dueDate: new Date(), completed: false };
  @Output() remove: EventEmitter<void> = new EventEmitter<void>();
  clickButton(path: string) {
    console.log("move")
    this.router.navigate([path]);
} 
}
<div class="todo-item">
  <div class="custom-checkbox">
    <input type="checkbox" [(ngModel)]="task.completed">
  </div>

 <div class="todo-title">
  <span  [ngClass]="{'completed': task.completed}">{{ task.title }}</span>
  <div class="todo-des">{{ task.description }}</div>
 </div>
  <span class="delete-icon">
    <i class="fas fa-edit m-2" (click)="clickButton('/update-task')"></i> 
    <i class="fas fa-trash-alt" (click)="remove.emit()" ></i>
</span>

</div>
