import { Component } from '@angular/core';
import { NgForm } from '@angular/forms';
import { Task } from '../../models/task.model'; 
import { Router} from '@angular/router';
import { TaskCommunicationService } from '../../task-communication.service'; 
@Component({
  selector: 'app-task-form',
  templateUrl: './task-form.component.html',
  styleUrls: ['./task-form.component.css']
})
export class TaskFormComponent {

  newTask: Task = { id: "", title: '', description: '', dueDate: new Date(), completed: false };
  showTitleWarning: boolean = false;
  constructor(private taskCommunicationService: TaskCommunicationService, private router: Router) {} 
  onSubmit(form: NgForm) {
    if (!this.newTask.title) {
      this.showTitleWarning = true; 
      return;
    }
    if (form.valid) {
    this.router.navigate(['/']);
    this.taskCommunicationService.addTask(this.newTask);
    this.newTask ={ id: "", title: '', description: '', dueDate: new Date(), completed: false };
    form.resetForm();
    this.showTitleWarning = false;
    }
  }
}
<div class="form-container">
  <!-- Form Title -->
  <h1>Add Task</h1>

  <!-- Task Form -->
  <form (ngSubmit)="onSubmit(taskForm)" #taskForm="ngForm" novalidate>
    <!-- Title Input -->
    <label for="title">Title</label>
    <input type="text" id="title" [(ngModel)]="newTask.title" name="title" required>

    <!-- Validation Message for Title -->
    <div class="validation-message" *ngIf="showTitleWarning">
      Please enter a title.
    </div>

    <!-- Description Input -->
    <label for="description">Description</label>
    <textarea id="description" [(ngModel)]="newTask.description" name="description"></textarea>

    <!-- Submit Button -->
    <button type="submit" [disabled]="taskForm.invalid">Add Task</button>
  </form>
</div>
